import React, { Component } from "react"
import { Button, Transition, Dropdown, Grid, Segment, List } from 'semantic-ui-react'
const users = ['ðŸ±', 'ðŸ¶', 'ðŸ· ', 'ðŸ¸']


export default class Start extends Component {

  state = { items: users.slice(0, 3) }

  handleAdd = () => this.setState(prevState => ({ items: users.slice(0, prevState.items.length + 1) }))

  handleRemove = () => this.setState(prevState => ({ items: prevState.items.slice(0, -1) }))

  render() {
     const { items } = this.state

    return(
      <Grid >
        <Grid.Row columns={1}>
          <Grid.Column>
            <Segment>

              <Button.Group>
              <Button disabled={items.length === 0} icon='minus' onClick={this.handleRemove} />
              <Button disabled={items.length === users.length} icon='plus' onClick={this.handleAdd} />
            </Button.Group>

            <Transition.Group as={List} duration={200} divided size='huge' verticalAlign='middle'>
              {items.map(item => (
                <List.Item key={item}>
                  <List.Content header={_.startCase(item)} />
                </List.Item>
              ))}
            </Transition.Group>

            </Segment>
          </Grid.Column>
          <Grid.Column>
            <Segment><Dropdown placeholder='Select Number of Players' fluid selection options={friendNumber}/></Segment>
          </Grid.Column>
          <Grid.Column>
            <Segment><Dropdown placeholder='Select Number of Players' fluid selection options={friendNumber}/></Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    )
  }
}






