import React, { Component } from "react"
import { Button, Segment, Dropdown, Grid } from 'semantic-ui-react'

const friendNumber = [
  {
    text: 'üê±',
    value: 1,
  },
  {
    text: 'üê± üê∂',
    value: 2,
  },
  {
    text: 'üê± üê∂ üê∑',
    value: 3,
  },
  {
    text: 'üê± üê∂ üê∑ üê∏',
    value: 4,
  }
]



export default class Start extends Component {



  render() {
    return(
      <Grid className=".ui.grid middle aligned">
        <Grid.Row columns={1}>
          <Grid.Column>
            <Segment><Dropdown placeholder='Select Number of Players' fluid selection options={friendNumber}/></Segment>
          </Grid.Column>
          <Grid.Column>
            <Segment><Dropdown placeholder='Select Number of Players' fluid selection options={friendNumber}/></Segment>
          </Grid.Column>
          <Grid.Column>
            <Segment><Dropdown placeholder='Select Number of Players' fluid selection options={friendNumber}/></Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    )
  }
}






